mapper.select:
select money,
tax,
uni_point,

type,
no
from tb_buy_point_history
where deleted_at is null
and user_no = #{user_no}

    and no = #{no}


    and type = #{type}

mapper.update:
update
    tb_buy_point_history
set updated_at = current_timestamp(),
    type       = #{type}
where no = #{no}

mapper.select:
select count(1) as count
from tb_user_sum
where user_no = #{value}
  and type = #{type}
