mapper.update:
update tb_wallet_info
set coin_value = #{coin_value}
where wallet_type = #{wallet_type}
  and coin_type = #{coin_type}

mapper.select:
select coin_value
from tb_wallet_info
where wallet_type = #{wallet_type}
  and coin_type = #{coin_type}

mapper.update:
update tb_source_Income_coin
set confirm_yn = 1
where mining_day = #{mining_day}
